mapper.select:
/* 등록된 파일중 각월마다 가장 최근 등록한 걸로 가져온다.*/
select cost_file,ad_id from cost where substr(cost_file,instr(cost_file,'_',1,3)+1,2)=#{keyword}
and	to_number(substr(cost_file,instr(cost_file,'_')+1,4))
= (select max(to_number(substr(cost_file,instr(cost_file,'_')+1,4)))
from cost where substr(cost_file,instr(cost_file,'_',1,3)+1,2)=#{keyword})
