mapper.select:
/*관리자 리스트*/
select ad_id, ad_name, ad_email, to_char(ad_date,'yy-mm-dd') ad_date, ad_tell, ad_grade
from admin

/*공통 멤버 리스트*/
select m_num, m_id, m_name, m_job, m_address, m_phone, m_email, m_date, mt
from mblist

/*개인 회원 리스트*/
select m_id, m_name, m_gender, m_job, m_email, m_phone, m_date, m_update
from member
