mapper.insert:
insert into login_history(m_num, m_id, retry, lastFailedLogin, lastSuccessedLogin, clientIP)
values(
login_history_seq.nextval, #{m_id}, #{retry,jdbcType=INTEGER}, 
#{lastFailedLogin,jdbcType=INTEGER}, #{lastSuccessedLogin,jdbcType=INTEGER}, 
#{clientIP,jdbcType=VARCHAR}
)

mapper.update:
update  login_history  
set retry = #{retry},
lastFailedLogin = #{lastFailedLogin},
lastSuccessedLogin = #{lastSuccessedLogin},
clientIP = #{clientIP,jdbcType=VARCHAR}
where m_num = #{m_num} and m_id = #{m_id}
