mapper.select:
/* member - memberSelect */

SELECT m_id FROM 
(SELECT m_id FROM GROUP_MEMBER UNION ALL SELECT m_id FROM member ) 
GROUP BY m_id HAVING m_id=#{m_id}

mapper.update:
update member set 

m_pwd=#{m_pwd}, 

m_phone=#{m_phone}, 
m_email=#{m_email} 
where m_id=#{m_id}

mapper.insert:
/*member - memberInsert*/
 
	select 'p_'||member_seq.nextval from dual
 
insert into member(m_num,m_id,m_pwd,m_name,m_birth,m_gender,m_email
,m_phone,m_job,m_zipcode,m_address,m_date,quiz,ans,cart,whish,mt)
values(#{m_num},#{m_id},#{m_pwd},#{m_name}
,#{m_birth},#{m_gender},#{m_email},#{m_phone}
,#{m_job},#{m_zipcode},#{m_address},sysdate,#{quiz},#{ans},'cart_'||member_seq.nextval,'whish_'||member_seq.nextval,'개인')

mapper.select:
/* groupMember - groupMemberSelect */
select m_num,m_id,m_pwd,m_name
from group_member where m_id=#{m_id}

mapper.update:
update group_member set 

m_pwd=#{m_pwd}, 

m_phone=#{m_phone}, 
m_email=#{m_email} 
where m_id=#{m_id}

mapper.select:
/* groupMember - groupComMemberSelect */
select m_name,com_no
from group_member where com_no=#{com_no}

mapper.insert:
/* groupMember - groupMemberInsert */
select 'g_'||group_member_seq.nextval from dual
insert into group_member(m_num,m_id,m_pwd,m_name,com_no,leader_name,leader_phone, m_address,m_zipcode,char_name,char_phone,char_email,acc_name,acc_phone,acc_email, m_job,m_date,fax,cart,whish,mt) values(#{m_num},#{m_id},#{m_pwd},#{m_name},#{com_no},#{leader_name},#{leader_phone}, #{m_address},#{m_zipcode},#{char_name},#{char_phone},#{char_email},#{acc_name},#{acc_phone},#{acc_email}, #{m_job},sysdate,#{fax},'cart_'||group_member_seq.nextval||'g','whish_'||group_member_seq.nextval||'g','단체')
